import { Mark } from "./types";

export class Opponent {
  private nextAttack: { x: number, y: number } | null = null;

  constructor(private size: { x: number, y: number }) {}

  makeMove = (board: Mark[][], lastMove: { x: number, y: number }): { x: number, y: number } => {
    const moves = this.getMoves(board);

    const a = this.whereAreMyMarksAround(board, lastMove.x, lastMove.y);
    const b = a?.find(({ x, y }) => board[2 * lastMove.y - y][2 * lastMove.x - x] === Mark.None);
    console.log('movesAround', a);
    console.log('moveWithNothingOpposite', b);

    const getRandomMove = () => moves[Math.floor(Math.random() * moves.length)];

    const move = this.shouldIBeAlamed(board, lastMove);
    if (move && moves.includes(move))
      return move;

    if (!this.nextAttack) {
      const randomMove = getRandomMove();
      const movesAround = this.whereAreMyMarksAround(board, randomMove.x, randomMove.y)

      if (!movesAround) {
    console.log('4', randomMove);
        this.nextAttack = this.getMovesAround(board, randomMove.x, randomMove.y)[0] || null
        return randomMove;
      }

      const moveWithNothingOpposite = movesAround?.find(({ x, y }) => board[2 * randomMove.y - y][2 * randomMove.x - x] === Mark.None);
      if (!moveWithNothingOpposite) {
        this.nextAttack = this.getMovesAround(board, randomMove.x, randomMove.y)[0] || null
        return randomMove;
      }

    console.log('3', this.nextAttack);
      this.nextAttack = moveWithNothingOpposite;
      return randomMove;
    }

    const movesAround = this.whereAreMyMarksAround(board, this.nextAttack.x, this.nextAttack.y);
    if (!movesAround) {
    console.log('2', this.nextAttack);
      const attackThisTurn = this.nextAttack;
      this.nextAttack = this.getMovesAround(board, this.nextAttack.x, this.nextAttack.y)[0] || null
      return attackThisTurn;
    }

    const moveWithNothingOpposite = movesAround?.find(({ x, y }) => board[2 * this.nextAttack!.y - y][2 * this.nextAttack!.x - x] === Mark.None);
    if (!moveWithNothingOpposite) {
    console.log('1', this.nextAttack);

      const attackThisTurn = this.nextAttack;
      this.nextAttack = this.getMovesAround(board, this.nextAttack.x, this.nextAttack.y)[0] || null
      return attackThisTurn;
    }

    console.log('5', this.nextAttack);

    const attackThisTurn = this.nextAttack;
    this.nextAttack = moveWithNothingOpposite;
    return attackThisTurn;
  }

  private getMoves = (board: Mark[][]): { x: number, y: number }[] => {
    const moves: { x: number, y: number }[] = [];

    for (let y = 0; y < board.length; y++) {
      const row = board[y];
      for (let x = 0; x < row.length; x++) {
        if (board[y][x] === Mark.None) moves.push({ x, y });
      }
    }

    return moves;
  }

  private tilesWithMarkAound = (board: Mark[][], around: { x: number, y: number }): { x: number, y: number }[] => {

  private getMovesAround = (board: Mark[][], x: number, y: number): { x: number, y: number }[] => {
    const possibilities = [
      { x: 1, y: 1 },
      { x: 0, y: 1 },
      { x: -1, y: 1 },
      { x: -1, y: 0 },
      { x: -1, y: -1 },
      { x: 0, y: -1 },
      { x: 1, y: -1 },
      { x: 1, y: 0 },
    ];

    const movesAround: { x: number, y: number }[] = [];
    for (const possibility of possibilities) {
      const newX = x + possibility.x;
      const newY = y + possibility.y;

      if (newX >= 0 && newX < this.size.x && newY >= 0 && newY < this.size.y)
        if (board[newY][newX] === Mark.None)
          movesAround.push({ x: newX, y: newY });
    }

    return movesAround;
  }

  private whereAreMyMarksAround = (board: Mark[][], x: number, y: number): { x: number, y: number }[] | null => {
    const possibilities = [
      { x: 1, y: 1 },
      { x: 0, y: 1 },
      { x: -1, y: 1 },
      { x: -1, y: 0 },
      { x: -1, y: -1 },
      { x: 0, y: -1 },
      { x: 1, y: -1 },
      { x: 1, y: 0 },
    ];

    const marksAround: { x: number, y: number }[] = [];
    for (const possibility of possibilities) {
      const newX = x + possibility.x;
      const newY = y + possibility.y;

      if (newX >= 0 && newX < this.size.x && newY >= 0 && newY < this.size.y)
        if (board[newY][newX] === Mark.X)
          marksAround.push({ x: newX, y: newY });
    }

    if (marksAround.length > 0)
      return marksAround

    return null;
  }

  private shouldIBeAlamed = (board: Mark[][], lastMove: { x: number, y: number }): { x: number, y: number } | null => {
    const { x, y } = lastMove;

    const danger = [
      [
        { x: 1, y: 1 },
        { x: 2, y: 2 },
      ],
      [
        { x: 0, y: 1 },
        { x: 0, y: 2 },
      ],
      [
        { x: -1, y: 1 },
        { x: -2, y: 2 },
      ],
      [
        { x: -1, y: 0 },
        { x: -2, y: 0 },
      ],
      [
        { x: -1, y: -1 },
        { x: -2, y: -2 },
      ],
      [
        { x: 0, y: -1 },
        { x: 0, y: -2 },
      ],
      [
        { x: 1, y: -1 },
        { x: 2, y: -2 },
      ],
      [
        { x: 1, y: 0 },
        { x: 2, y: 0 },
      ],
    ];

    for (const direction of danger) {
      if (
        board[y + direction[0].y]?.[x + direction[0].x] === Mark.X
        && board[y + direction[1].y]?.[x + direction[1].x] === Mark.X
      ) {
        if (board[y + direction[0].y]?.[x + direction[0].x] === Mark.None) {
          return {
            x: x + direction[0].x,
            y: y + direction[0].y,
          };
        } else if (board[y + direction[1].y * 3]?.[x + direction[1].x * 3] === Mark.None) {
          return {
            x: x - direction[1].x * 3,
            y: y - direction[1].y * 3,
          };
        }
      }
    }

    return null
  }
}
