import { Component, Input } from '@angular/core';

@Component({
  selector: 'app-game',
  template: `
    {{x}}, {{y}}
  `,
  styles: [
    ``,
  ],
})
export class GameComponent {
  @Input('x') x?: number = 5;
  @Input('y') y?: number = 5;

  ngOnInit() {
    if (
      !this.x || !this.y
      || this.x < 1 || this.y < 1
      || this.x < 5 && this.y < 5
    ) {
      this.x = 5;
      this.y = 5;
    }
  }
}
