import { Mark } from "./types";

export class Opponent {
  private nextAttack: { x: number, y: number } | null = null;

  constructor(private size: { x: number, y: number }) {}

  makeMove = (board: Mark[][], lastMove: { x: number, y: number }): { x: number, y: number } => {
    const moves = this.getMoves(board);

    const getRandomMove = () => moves[Math.floor(Math.random() * moves.length)];

    const move = this.shouldIBeAlamed(board, lastMove);
    if (move && moves.includes(move))
      return move;

    if (!this.nextAttack) {
      const thisAttack = getRandomMove()
      // losuj następny atak
      return thisAttack
    } else {
      const thisAttack = this.nextAttack
      // losuj nastepny atak
      return thisAttack
    }
  }

  private getMoves = (board: Mark[][]): { x: number, y: number }[] => {
    const moves: { x: number, y: number }[] = [];

    for (let y = 0; y < board.length; y++) {
      const row = board[y];
      for (let x = 0; x < row.length; x++) {
        if (board[y][x] === Mark.None) moves.push({ x, y });
      }
    }

    return moves;
  }

  private tilesWithMarkAound = (board: Mark[][], around: { x: number, y: number }, mark: Mark): { x: number, y: number }[] => {
    const { x, y } = around;

    const possibilities = [
      { x: 1, y: 1 },
      { x: 0, y: 1 },
      { x: -1, y: 1 },
      { x: -1, y: 0 },
      { x: -1, y: -1 },
      { x: 0, y: -1 },
      { x: 1, y: -1 },
      { x: 1, y: 0 },
    ];

    const tilesAround: { x: number, y: number }[] = [];
    for (const possibility of possibilities) {
      const newX = x + possibility.x;
      const newY = y + possibility.y;

      if (newX >= 0 && newX < this.size.x && newY >= 0 && newY < this.size.y)
        if (board[newY][newX] === mark)
          tilesAround.push({ x: newX, y: newY });
    }

    return tilesAround;
  }

  private getMovesAround = (board: Mark[][], x: number, y: number): { x: number, y: number }[] =>
    this.tilesWithMarkAound(board, { x, y }, Mark.None);

  private getMyMarksAround = (board: Mark[][], x: number, y: number): { x: number, y: number }[] =>
    this.tilesWithMarkAound(board, { x, y }, Mark.X);

  private getOppositeMoves = (moves: { x: number, y: number }[], origin: { x: number, y: number }): { x: number, y: number }[] =>
    moves.map(({ x, y }) => ({
      x: 2 * origin.x - x,
      y: 2 * origin.y - y,
    }))
  private getOppositeFreeMoves = (board: Mark[][], origin: { x: number, y: number }): { x: number, y: number }[] =>
    this.getOppositeMoves(this.getMovesAround(board, origin.x, origin.y), origin).filter(({ x, y }) => board[y][x] === Mark.None)

  private shouldIBeAlamed = (board: Mark[][], lastMove: { x: number, y: number }): { x: number, y: number } | null => {
    const { x, y } = lastMove;

    const danger = [
      [
        { x: 1, y: 1 },
        { x: 2, y: 2 },
      ],
      [
        { x: 0, y: 1 },
        { x: 0, y: 2 },
      ],
      [
        { x: -1, y: 1 },
        { x: -2, y: 2 },
      ],
      [
        { x: -1, y: 0 },
        { x: -2, y: 0 },
      ],
      [
        { x: -1, y: -1 },
        { x: -2, y: -2 },
      ],
      [
        { x: 0, y: -1 },
        { x: 0, y: -2 },
      ],
      [
        { x: 1, y: -1 },
        { x: 2, y: -2 },
      ],
      [
        { x: 1, y: 0 },
        { x: 2, y: 0 },
      ],
    ];

    for (const direction of danger) {
      if (
        board[y + direction[0].y]?.[x + direction[0].x] === Mark.X
        && board[y + direction[1].y]?.[x + direction[1].x] === Mark.X
      ) {
        if (board[y + direction[0].y]?.[x + direction[0].x] === Mark.None) {
          return {
            x: x + direction[0].x,
            y: y + direction[0].y,
          };
        } else if (board[y + direction[1].y * 3]?.[x + direction[1].x * 3] === Mark.None) {
          return {
            x: x - direction[1].x * 3,
            y: y - direction[1].y * 3,
          };
        }
      }
    }

    return null
  }
}
