import { Component, EventEmitter, Input, Output } from '@angular/core';
import { Mark } from '../types';

@Component({
  selector: 'app-game-bar',
  template: `
    <div id="score">
      <div id="x">
        <img src="/assets/x.png" />
        <span>{{ score['X'] }}</span>
      </div>
      <div id="o">
        <img src="/assets/o.png" />
        <span>{{ score['O'] }}</span>
      </div>
    </div>

    <button id="restart" (click)="restart()">restart</button>
  `,
  styles: [
    `
      :host {
        display: flex;
        align-items: center;

        gap: 1rem;

        font-size: 2rem;
      }

      :host #score {
        flex: 1;
        width: 100%;
        display: flex;
        justify-content: space-between;
        align-items: center;
      }

      :host #score > div {
        display: flex;
        justify-content: space-between;
        align-items: center;

        padding: 0.5rem;
      }

      :host #score > div > img {
        width: 2rem;
        height: 2rem;
      }

      #x {
        background-color: #D08770;
        border-radius: 50px 0 0 50px;
      }
      #o {
        background-color: #A3BE8C;
        border-radius: 0 50px 50px 0;
      }

      :host #restart {
        flex: 0;
        width: 100px;
        height: 100px;
        border-radius: 50px;
        border: 1px solid black;
        background-color: #EBCB8B;
      }
    `,
  ],
})
export class GameBarComponent {
  @Input() score: { [Mark.X]: number, [Mark.O]: number } = { [Mark.X]: 0, [Mark.O]: 0 };

  @Output() restartChange = new EventEmitter<void>();
  restart = () => this.restartChange.emit();
}
