import { Component } from '@angular/core';
import { HttpClient } from '@angular/common/http';

import { MagazinesDB } from './helpers';

@Component({
    selector: 'app-root',
    template: `
        {{ czasopisma.getMagazine('Avax') | json }}
        <app-magazines [db]="czasopisma"></app-magazines>
    `,
    styles: [`
    `],
})
export class AppComponent {
    czasopisma: MagazinesDB

	constructor(private http: HttpClient) {
        this.czasopisma = new MagazinesDB(http);
    }
}
