import { Component, Input } from '@angular/core';
import { MagazinesDB } from './helpers';

@Component({
    selector: 'app-magazines',
    template: `
        {{ db?.get() | json }}
        <div *ngFor="let czasopismo of db?.get()">
            <h1>{{ czasopismo.name }}</h1>
            <img [src]="czasopismo.thumbnail" />
            <div *ngFor="let year of czasopismo.years">
        </div>
    `,
    styles: [`
    `],
})
export class AppMagazines {
    @Input() db: MagazinesDB | null = null

