import { Component, Input } from '@angular/core';
import { MagazinesDB } from './helpers';

@Component({
    selector: 'app-magazines',
    template: `
        <div id="magazines-grid" *ngFor="let magazine of db?.get()">
            <h1>{{ magazine.name }}</h1>
            <img [src]="magazine.thumbnail" />
        </div>
    `,
    styles: [`
        #magazines-grid {
            display: grid;
            grid-template-columns: repeat(auto-fill, minmax(200px, 1fr));
            grid-gap: 1rem;
        }
    `],
})
export class AppMagazines {
    @Input() db: MagazinesDB | null = null

	constructor() {
    }
}
