import { HttpClient } from '@angular/common/http';
import * as xml2js from 'xml2js';

const DATA_URL = '/assets/czasopisma.xml';

class Czasopisma {
    public czasopisma: Czasopismo[] = [];
    public get = ({
        name
    }: {
        name?: string
    } = {}) => this.czasopisma
        .filter(czasopismo => (
            name? czasopismo.name === name: true
        ));

	constructor(private http: HttpClient) {
        this.load();
    }

    private load = () =>
        this.http.get(DATA_URL, { responseType: 'text' })
            .subscribe(data => {
                const rawCzasopisma = this.convertToJSON(data).czasopisma

                this.czasopisma = Object.entries(rawCzasopisma).reduce((acc, [name, value]) => {
                    if (name === 'zmienne' || name === 'lata')
                        return acc;

                    console.log(value);
                    const publications = Object.values(value as any[])
                    console.log(publications);
                    const czasopismo = new Czasopismo(name, publications)
                    acc.push(czasopismo);
                    return acc;
                }, [] as Czasopismo[])
            });

    private convertToJSON = (xml: string) => {
        let res: any = null

        const parser = new xml2js.Parser({explicitArray: false});
        parser.parseString(xml, (err, result) => res = result);

        return res;
    }
}

class Czasopismo {
    constructor(public name: string, public publications: any[]) {}
}

class Publication {
    constructor(
        name: string,
        number: string,
        thumbnail: string,
        file: string,
        format: string,
    ) {}
}

export { Czasopisma };
