import { HttpClient } from '@angular/common/http';
import * as xml2js from 'xml2js';

const DATA_URL = 'https://mendela.pl/ap_kli/czasopisma.xml';

class Czasopisma {
    public czasopisma: Czasopismo[];
    public get = () => {
        console.log(this.czasopisma);
        return this.czasopisma;
    }

	constructor(private http: HttpClient) {
        this.http.get(DATA_URL, { responseType: 'text' })
            .subscribe(data => {
                const rawCzasopisma = this.convertToJSON(data).czasopisma

                this.czasopisma = Object.keys(rawCzasopisma).map(key => {
                    const czasopismo = rawCzasopisma[key];
                    return new Czasopismo(czasopismo.name);
                })
            });
    }

    private convertToJSON = (xml: string) => {
        let res: any = null

        const parser = new xml2js.Parser({explicitArray: false});
        parser.parseString(xml, (err, result) => res = result);

        return res;
    }
}

class Czasopismo {
    constructor(public name: string) {

    }
}

export { Czasopisma };
