import { Component, EventEmitter, Input, Output } from '@angular/core';

import { Magazine } from './helpers';

@Component({
    selector: 'app-magazine',
    template: `
        <div id="years-select">
            <div
                *ngFor="let year of magazine?.years"
            >
                <input
                    type="checkbox"
                    id="{{ year?.year }}"
                />
                <label for="{{ year?.year }}">{{ year?.year }}</label>
            </div>
        </div>

        <div id="years">
            <div
                *ngFor="let year of magazine?.years"
            >

                <div class="year">
                    <h2>{{ year?.year }}</h2>

                    <app-publication
                        *ngFor="let publication of year?.publications"
                        [publication]="publication"
                    />
                </div>
            </div>
        </div>
    `,
    styles: [`
        :host {
            display: flex;
            flex-direction: column;
            gap: 1rem;
        }

        #years-select {
            display: flex;
            flex-direction: row;
            gap: 1rem;
        }

        #years {
            display: flex;
            flex-direction: row;
            gap: 1rem;
        }
        .year {
            display: flex;
            flex-direction: column;
            gap: 1rem;
        }
    `],
})
export class AppMagazine {
    @Input() magazine: Magazine | null = null

    protected showYears: string[] = []
}
