import { Component } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import * as xml2js from 'xml2js';

const DATA_URL = 'https://mendela.pl/ap_kli/czasopisma.xml';

@Component({
    selector: 'app-root',
    template: `
        {{ czasopisma.zmienne | json }}
    `,
    styles: [`
    `],
})
export class AppComponent {
    protected czasopisma: any = null;

	constructor(private http: HttpClient) {
        this.load();
    }

    load = async () =>
        this.http.get(DATA_URL, { responseType: 'text' })
            .subscribe(data => this.czasopisma = this.convertToJSON(data).czasopisma);

    convertToJSON = (xml: string) => {
        let res: any = null

        const parser = new xml2js.Parser({explicitArray: false});
        parser.parseString(xml, (err, result) => res = result);

        return res;
    }
}
