import { Component } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import * as xml2js from 'xml2js';

const DATA_URL = 'https://mendela.pl/ap_kli/czasopisma.xml';

@Component({
    selector: 'app-root',
    template: `
        {{ data | json }}
    `,
    styles: [`
    `],
})
export class AppComponent {
    protected data: any = '';

	constructor(private http: HttpClient) {
        this.load();
    }

    load = async () =>
        this.http.get(DATA_URL, { responseType: 'text' })
            .subscribe(
                data => this.data = this.convertToJSON(data),
            );

    convertToJSON = async (xml: string) => {
        const parser = new xml2js.Parser({explicitArray: false});

        let res = {}

        parser.parseString(xml, (err, result) => res = result);
    }
}
