import { HttpClient } from '@angular/common/http';
import * as xml2js from 'xml2js';

const DATA_URL = '/assets/czasopisma.xml';
const ASSETS_URL = 'http://atarionline.pl/biblioteka/czasopisma/img/'

class MagazinesDB {
    public czasopisma: Magazine[] = [];

    public get = () => this.czasopisma;
    public getNames = () => this.czasopisma
        .map(czasopismo => czasopismo.name);
    public getMagazine = (name: string) => this.czasopisma
        .find(czasopismo => czasopismo.name === name);

	constructor(private http: HttpClient) {
        this.load();
    }

    private load = () =>
        this.http.get(DATA_URL, { responseType: 'text' })
            .subscribe(data => {
                const rawCzasopisma = this.convertToJSON(data).czasopisma

                this.czasopisma = Object.entries(rawCzasopisma).reduce((acc, [name, value]) => {
                    if (name === 'zmienne' || name === 'lata')
                        return acc;

                    const publications = Object.values(value as any[])
                        .map(publication => new Publication(
                            publication.nazwa,
                            publication.numer,
                            parseInt(publication.stron),

                            ASSETS_URL + publication.miniaturka,
                            publication.format,
                            publication.plik,

                            publication.podeslal,
                            publication.przetworzenie,
                            publication.skan,
                            publication.wydawca,
                        ))
                        .filter(publication => publication.name !== 'index');
                    const czasopismo = new Magazine(
                        name,
                        ASSETS_URL + rawCzasopisma.zmienne[name].src,
                        rawCzasopisma.lata[name].split(',')
                            .map((year: string) => new Year(
                                parseInt(year),
                                publications.filter(publication => publication.name.startsWith(year))
                            )),
                    )

                    acc.push(czasopismo);
                    return acc;
                }, [] as Magazine[])
            });

    private convertToJSON = (xml: string) => {
        let res: any = null

        const parser = new xml2js.Parser({explicitArray: false});
        parser.parseString(xml, (err, result) => res = result);

        return res;
    }
}

class Magazine {
    constructor(
        public name: string,
        public thumbnail: string,
        public years: Year[],
    ) {}
}

class Year {
    constructor(
        public year: number,
        public publications: Publication[],
    ) {}
}

class Publication {
    constructor(
        public name: string,
        public number: string,
        public pages: number,

        public thumbnail: string,
        public format: string,
        public file: string,

        public uploader: string,
        public editor: string,
        public scanner: string,
        public publisher: string,
    ) {}
}

export { MagazinesDB, Magazine, Publication };
