import './App.css'
import {useState, useEffect} from 'react'

import ItemsList from './ItemsList'

function App() {
    const [items, setItems] = useState(defaultItems)

    const addItem = (title, content) =>
        setItems([
            ...items,
            {
                title,
                content,
            },
        ])

    useEffect(() => {
        addItem('Item 3', 'This is the content of item 3')
    }, [])

    return (
        <div className='App'>
            <ItemsList
                items={items}
                onRemove={(key) =>
                    setItems(items.filter((_, index) => index !== key))
                }
            />
        </div>
    )
}

export default App
