import './App.css'
import {useState} from 'react'

import ItemsList from './ItemsList'

function App() {
    const defaultItems = [
        {
            title: 'Item 2',
            content: 'This is the content of item 2',
        },
        {
            title: 'Item 1',
            content: 'This is the content of item 1',
        },
    ]
    const [items, setItems] = useState(defaultItems)

    return (
        <div className='App'>
            <ItemsList
                items={items}
                onRemove={(key) => {
                    const newItems = [...items].slice(key, 1)
                    setItems(newItems)
                }}
            />
        </div>
    )
}

export default App
