import './App.css'
import {useState} from 'react'

import ItemsList from './ItemsList'

function App() {
    const [items, setItems] = useState([
        {
            title: 'Item 2',
            content: 'This is the content of item 2',
        },
        {
            title: 'Item 1',
            content: 'This is the content of item 1',
        },
    ])

    const addItem = (title, content, where) => {
        setItems([
            ...items,
            {
                title,
                content,
            },
        ])
    }

    return (
        <div className='App'>
            <div className='actions'>
                <button
                    onClick={() =>
                        addItem('Item 3', 'This is the content of item 3')
                    }
                >
                    add to the end
                </button>
                <button
                    onClick={() =>
                        addItem('Item 3', 'This is the content of item 3')
                    }
                >
                    add to the beginning
                </button>
            </div>

            <ItemsList
                items={items}
                onRemove={(key) =>
                    setItems(items.filter((_, index) => index !== key))
                }
            />
        </div>
    )
}

export default App
